import styles from './Header.module.css' ;
import { Link } from 'react-router-dom';

// App.js로부터 isLoggedIn, username, onLogout 함수를 props로 받습니다.
const Header = ({ isLoggedIn, username, onLogout }) => {

    // const handleRefresh = (e) => {
    // // a 태그의 기본 동작(페이지 상단으로 이동)을 막습니다.
    // e.preventDefault(); 
    // // 페이지를 강제로 새로고침합니다.
    // window.location.reload();
    // };


    return (
        <>
            <header>
                <h1>
                    {isLoggedIn?(
                    <Link to='/main'>G</Link>):
                    (<Link to='/'>G</Link>)
                    }
                </h1>
                {isLoggedIn? (
                <div className='styles.full-user-menu'>
                    <div className='styles.top-menu-container'>
                        <span class><Link to="/community" className='styles.top-menu-button'>커뮤니티</Link></span>
                    </div>
                    <div className="styles.user-menu">
                            
                        <span>{username}님 환영합니다!</span>
                        <Link to="/"><button onClick={onLogout}>로그아웃</button></Link>
                    </div>
                </div>
                    ) : (
                        <></>
                    )}
            </header>
        </>
    );
};

export default Header;
