import './Login.css'; // CSS 파일을 불러옵니다.
import { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Login = ({ onLogin }) => {
  const [userName, setUserName] = useState('')
  const [password, setPassword] = useState('')


  const handleLogin = async (e) => {
    e.preventDefault();

    
    const userData = {
      username: userName,
      password: password
    };


    try{ 
      const response = await axios.post('http://127.0.0.1:8000/login', userData);
      if(response.data.res === 1){
        localStorage.setItem('username', userName);
        let username = userName
        onLogin(username);
      }else {
          // --- ⬇️ 실패 처리 로직 추가 ⬇️ ---
          // 서버가 보내준 실패 메시지가 있다면 보여주고, 없다면 기본 메시지를 보여줍니다.
          alert(response.data.message || "아이디 또는 비밀번호가 일치하지 않습니다.");
        }

    }catch(err){
      console.error("로그인 에러", err);
      alert("로그인 실패")

    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <Link to="/"><h1 className="login-logo">Green</h1></Link>
        <form className="login-form" onSubmit={handleLogin}>
          <div className="input-group">
            <input 
              type="text" 
              placeholder="아이디" 
              value={userName}
              onChange={(e)=> setUserName(e.target.value)}
            />
          </div>
          <div className="input-group">
            <input 
              type="password" 
              placeholder="비밀번호" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="submit" className="login-button">
            로그인
          </button>
        </form>
        <div className="login-options">
          <Link to='#'>비밀번호 찾기</Link>
          <span>|</span>
          <Link to='/join'>회원가입</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
