import { Routes, Route } from "react-router-dom";
import { useState, useEffect } from 'react';
import Main  from "./pages/main.jsx";
import Login from "./pages/login.jsx"
import Join from "./pages/join.jsx";
import Header from "./pages/header.jsx";
import PrivateRoute from "./components/PrivateRoute.jsx";
import LoginCantLogin from "./components/PrivateRoute.jsx";

function App() {

    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');

    useEffect(() => {
        const storedUsername = localStorage.getItem('username');
        if (storedUsername) {
            setIsLoggedIn(true);
            setUsername(storedUsername);
        }
    }, []);

    const handleLogin = (loggedInUsername) => {
        console.log('handleLogin',loggedInUsername)
        setIsLoggedIn(true);
        setUsername(loggedInUsername);
    };

    const handleLogout = () => {
        localStorage.removeItem('username');
        setIsLoggedIn(false);
        setUsername('');
    };



  return (
    <div>
      <Header isLoggedIn={isLoggedIn} username={username} onLogout={handleLogout}/>
      <Routes>
        <Route path="/main" element={<PrivateRoute isLoggedIn={isLoggedIn}><Main/></PrivateRoute>}/>
        <Route path="/join" element={<LoginCantLogin isLoggedIn={isLoggedIn}><Join/></LoginCantLogin>}/>
        <Route path="/" element={<Login onLogin={handleLogin}/>}/>
      </Routes>
    </div>
  );
}

export default App;
