import React from 'react';
import './main.css';
import Header from './header';

// 돋보기 아이콘 (SVG)
const SearchIcon = () => (
  <svg viewBox="0 0 24 24" width="22" height="22">
    <path fill="#ffffff" d="M15.5 14h-.79l-.28-.27A6.471 6.471 0 0 0 16 9.5 6.5 6.5 0 1 0 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z"/>
  </svg>
);

const Main = () => {
  // 검색 기능을 여기에 추가할 수 있습니다 (예: Google 검색)
  const handleSearch = (event) => {
    if (event.key === 'Enter') {
      const query = event.target.value;
      window.location.href = `https://www.google.com/search?q=${query}`;
    }
  };

  return (
<>
    <Header/>
    <div className="mypage-container">
      <main className="main-content">
        <a href='/main'><h1 className="logo">Green</h1></a>
        <div className="search-bar">
          <input 
            type="text" 
            className="search-input" 
            placeholder="검색어를 입력하세요"
            onKeyDown={handleSearch}
          />
          <button className="search-button" onClick={() => {
            const query = document.querySelector('.search-input').value;
            if (query) window.location.href = `https://www.google.com/search?q=${query}`;
          }}>
            <SearchIcon />
          </button>
        </div>
        <div className="shortcut-links">
          <a href="https://mail.google.com" className="link-item">Gmail</a>
          <a href="https://www.youtube.com" className="link-item">YouTube</a>
          <a href="https://github.com" className="link-item">GitHub</a>
          <a href="https://www.instagram.com" className="link-item">Instagram</a>
        </div>
      </main>
    </div>
</>
  );
};

export default Main;
